/* Water Explosion Animation */
@keyframes waterExplode {
    0% {
        transform: scale(1) rotate(0deg);
        opacity: 1;
        filter: blur(0px);
    }

    25% {
        transform: scale(1.3) rotate(90deg);
        opacity: 0.8;
        filter: blur(1px);
        box-shadow:
            0 0 20px rgba(0, 125, 186, 0.6),
            0 0 40px rgba(0, 125, 186, 0.4),
            0 0 60px rgba(0, 125, 186, 0.2);
    }

    50% {
        transform: scale(1.8) rotate(180deg);
        opacity: 0.6;
        filter: blur(3px);
        box-shadow:
            0 0 30px rgba(0, 125, 186, 0.8),
            0 0 60px rgba(0, 125, 186, 0.6),
            0 0 90px rgba(0, 125, 186, 0.4);
    }

    75% {
        transform: scale(2.5) rotate(270deg);
        opacity: 0.3;
        filter: blur(5px);
        box-shadow:
            0 0 40px rgba(0, 125, 186, 1),
            0 0 80px rgba(0, 125, 186, 0.8),
            0 0 120px rgba(0, 125, 186, 0.6);
    }

    100% {
        transform: scale(3.5) rotate(360deg);
        opacity: 0;
        filter: blur(8px);
        box-shadow:
            0 0 60px rgba(0, 125, 186, 0.4),
            0 0 120px rgba(0, 125, 186, 0.2),
            0 0 180px rgba(0, 125, 186, 0.1);
    }
}

@keyframes waterDroplets {
    0% {
        opacity: 1;
        transform: translateY(0) scale(1);
    }

    100% {
        opacity: 0;
        transform: translateY(-50px) scale(0.3);
    }
}

.water-explosion {
    position: relative;
    overflow: visible;
}

.water-explosion::before {
    content: '';
    position: absolute;
    top: 50%;
    left: 50%;
    transform: translate(-50%, -50%);
    font-size: 1.5rem;
    animation: waterExplode 0.6s ease-out forwards;
    pointer-events: none;
    z-index: 1000;
}

.water-explosion::after {
    content: '';
    position: absolute;
    top: 50%;
    left: 50%;
    transform: translate(-50%, -50%);
    font-size: 0.8rem;
    animation: waterDroplets 0.8s ease-out 0.2s forwards;
    pointer-events: none;
    z-index: 999;
    letter-spacing: 10px;
}

.signal-card.exploding {
    animation: fadeOut 0.8s ease-out forwards;
}

@keyframes fadeOut {
    0% {
        opacity: 1;
        transform: scale(1);
    }

    50% {
        opacity: 0.7;
        transform: scale(1.02);
    }

    100% {
        opacity: 0;
        transform: scale(0.95);
    }
}

